(window.webpackJsonp=window.webpackJsonp||[]).push([[25],{570:function(n,e,t){"use strict";t.r(e),e.default="// Author @patriciogv ( patriciogonzalezvivo.com ) - 2015\n// Title: Diamond Tiles\n\n#ifdef GL_ES\nprecision mediump float;\n#endif\n\n#define PI 3.14159265358979323846\n\nuniform vec2 dd_resolution;\nuniform float dd_time;\n\nvec2 rotate2D(vec2 _st, float _angle){\n    _st -= 0.5;\n    _st =  mat2(cos(_angle),-sin(_angle),\n      sin(_angle),cos(_angle)) * _st;\n    _st += 0.5;\n    return _st;\n}\n\nvec2 tile(vec2 _st, float _zoom){\n    _st *= _zoom;\n    return fract(_st);\n}\n\nfloat box(vec2 _st, vec2 _size, float _smoothEdges){\n    _size = vec2(0.5)-_size*0.5;\n    vec2 aa = vec2(_smoothEdges*0.5);\n    vec2 uv = smoothstep(_size,_size+aa,_st);\n    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);\n    return uv.x*uv.y;\n}\n\nvec2 offset(vec2 _st, vec2 _offset){\n    vec2 uv;\n\n    if(_st.x>0.5){\n        uv.x = _st.x - 0.5;\n    } else {\n        uv.x = _st.x + 0.5;\n    }\n\n    if(_st.y>0.5){\n        uv.y = _st.y - 0.5;\n    } else {\n        uv.y = _st.y + 0.5;\n    }\n\n    return uv;\n}\n\nvoid main(void){\n    vec2 st = gl_FragCoord.xy/dd_resolution.xy;\n    st.y *= dd_resolution.y/dd_resolution.x;\n\n    st = tile(st,10.);\n\n    vec2 offsetSt = offset(st,vec2(0.5));\n\n    st = rotate2D(st,PI*0.25);\n\n    vec3 color = vec3( box(offsetSt,vec2(0.95),0.01) - box(st,vec2(0.3),0.01) + 2.*box(st,vec2(0.2),0.01) );\n\n    gl_FragColor = vec4(color,1.0);\n}\n"}}]);